//+++++++++++++++++++++++++++++++++++++++++++++| MAIN |+++++++++++++++++++++++++++++++++++++++++++++++
task main()
{
	wait1Msec(2000);						// Robot waits for 2000 milliseconds before executing program

	// Turn Right at full power for 0.75 seconds

	turn_right(750);

	turn_left(750);

	go_straight(FULL_SPEED, 2000);

	stop_movement(1000);
}




